import React, {Component} from 'react';

class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {clicked: false}
    }
    render() {
        return (
            <button
                onClick = {function () {
                    alert('Clicked!');
                }}
            >
                Click Me!
            </button>
        );
    }
}

export default Button;
